create table medicos (id bigint generated by default as identity, bairro varchar(255), cep varchar(255), cidade varchar(255), complemento varchar(255), crm varchar(255), email varchar(255), logradouro varchar(255), nome varchar(255), numero varchar(255), uf varchar(255), especialidade enum ('CARDIOLOGIA','DERMATOLOGIA','GINECOLOGIA','ORTOPEDIA'), primary key (id));
